class MetodoPeer extends BaseMetodoPeer {


	/**
	 *@author:maryit sanchez
	 *@date:6 de enero de 2011
	 *Esta funcion retorna  un listado de los metodos
	 */
	public static function listarMetodosActivos()
	{
		$fila=0;
		$datos;
		try{
			$conexion = new Criteria();
			$conexion->addDescendingOrderByColumn(MetodoPeer::MET_NOMBRE);
			$maquinas = MetodoPeer::doSelect($conexion);
			
			foreach($maquinas as $temporal)
			{
					$datos[$fila]['met_codigo'] = $temporal->getMetCodigo();
					$datos[$fila]['met_nombre'] = $temporal->getMetNombre();		
					$fila++;
			}
			
			$datos[$fila]['met_codigo']='';
			$datos[$fila]['met_nombre'] ='TODOS';
			$fila++;
			
		}
		catch (Exception $excepcion)
		{
			//return "({success: false, errors: { reason: 'Hubo una excepci&oacute;n en listar metodos ',error:'".$excepcion->getMessage()."'}})";
		}
		return $datos;
	}
} // MetodoPeer
