    public static function calcularEfectividadGlobalEquipo($disponibilidad, $eficiencia, $calidad)
    {
        $OEE = $disponibilidad;
        $OEE *= $eficiencia;
        $OEE *= $calidad;
        $OEE /= 10000;
        return $OEE;
    }

    public static function calcularCalidad($numeroInyecciones, $numeroReinyecciones)
