    public static function calcularTPMesEnHoras($codigoMaquina, $mes, $año, $params, $inyeccionesEstandarPromedio)
    {
        $registros = RegistroUsoMaquinaPeer::consultarRegistrosMes($codigoMaquina, $mes, $año, $params);

