    public static function obtenerCantidadMaxDiasMes($mes, $anio)
    {
        $cant_dias = 0;
        for ($dia = 31; $dia > 26; $dia--)
        {
            if (checkdate($mes, $dia, $anio))
            {
                $cant_dias = $dia;
                $dia = 26;
            }
        }
        return $cant_dias + 1;
    }

